mapper.select:
SELECT * FROM
(
SELECT ROW_NUMBER() OVER (ORDER BY GIFT_CARD_GRANT_DATE DESC)
AS RN,
GIFT_CARD.* FROM GIFT_CARD WHERE MEMBER_ID = #{memberId}
)
WHERE RN BETWEEN
#{start} AND #{end}

		SELECT * 
    	FROM (
        	SELECT ROW_NUMBER() OVER (ORDER BY GIFT_CARD_GRANT_DATE DESC) AS RN, 
               GIFT_CARD.* 
        	FROM GIFT_CARD 
        	WHERE MEMBER_ID = #{memberId} 
          AND (SYSDATE >= GIFT_CARD_EXPIRED_DATE OR BALANCE = 0)
    	)
    	WHERE RN BETWEEN #{start} AND #{end}

	SELECT * 
	FROM (
    	SELECT ROW_NUMBER() OVER (ORDER BY GIFT_CARD_GRANT_DATE DESC) AS RN, 
           GIFT_CARD.* 
    FROM GIFT_CARD 
    WHERE MEMBER_ID = #{memberId} 
      AND IS_USED = 1 
      AND BALANCE != 0
	)
	WHERE RN BETWEEN #{start} AND #{end}
