mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId} AND EMAIL = #{email}

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{password}
WHERE
MEMBER_ID = #{memberId}

UPDATE MEMBER
SET EMAIL = #{email}
WHERE MEMBER_ID =
#{memberId}

UPDATE MEMBER
SET PHONE = #{phone}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET IS_ENABLED = 0
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET IS_ENABLED = 1
WHERE MEMBER_ID = #{memberId}
