mapper.select:
        SELECT * FROM
		(
		SELECT ROW_NUMBER() OVER (ORDER BY ORDER_DATE_CREATED DESC)
		AS RN, ORDERS.* FROM ORDERS
		)
		WHERE RN BETWEEN #{start} AND #{end}

SELECT ORDER_ID, FOOD_ID, ORDER_AMOUNT, ORDER_DATE_CREATED, OWNER_ID, APPROVAL_STATUS 
FROM ORDERS
WHERE OWNER_ID = #{ownerId}

SELECT * FROM ORDERS
WHERE OWNER_ID = #{ownerId}
AND ORDER_ID BETWEEN #{pagination.start} AND #{pagination.end}
ORDER BY ORDER_DATE_CREATED DESC

SELECT COUNT(*) 
FROM ORDERS
WHERE OWNER_ID = #{ownerId}

mapper.update:
UPDATE ORDERS
SET APPROVAL_STATUS = #{approvalStatus}
WHERE ORDER_ID = #{orderId}
