mapper.select:
SELECT * FROM
(
	SELECT ROW_NUMBER() OVER (ORDER BY QUESTION_ID DESC)
	AS RN, QUESTION.* FROM QUESTION
) 
WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM
(
SELECT ROW_NUMBER() OVER (ORDER BY QUESTION_ID DESC)
AS RN, QUESTION.* FROM QUESTION WHERE MEMBER_ID = #{memberId}
)
WHERE RN BETWEEN #{start} AND #{end}
