mapper.insert:
INSERT INTO REVIEW
(REVIEW_ID, MEMBER_ID,
REVIEW_TITLE, REVIEW_CONTENT, FOOD_ID, REVIEW_RATING)
VALUES(REVIEW_SEQ.NEXTVAL,
#{memberId},#{reviewTitle},#{reviewContent},#{foodId},#{reviewRating}
)

mapper.update:
UPDATE REVIEW SET
REVIEW_TITLE = #{reviewTitle},
REVIEW_CONTENT = #{reviewContent}, REVIEW_RATING = #{reviewRating}
WHERE REVIEW_ID = #{reviewId}

UPDATE REVIEW SET
REVIEW_TITLE = #{reviewTitle}
WHERE
REVIEW_ID = #{reviewId}

UPDATE REVIEW SET
REVIEW_CONTENT = #{reviewContent}
WHERE REVIEW_ID = #{reviewId}

UPDATE REVIEW SET
REVIEW_RATING = #{reviewRating}
WHERE REVIEW_ID = #{reviewId}

UPDATE REVIEW SET
REVIEW_IMG_PATH = #{reviewImgPath}
WHERE REVIEW_ID = #{reviewId}

mapper.select:
SELECT * FROM
(
SELECT ROW_NUMBER() OVER (ORDER BY REVIEW_ID DESC)
AS RN, REVIEW.* FROM REVIEW WHERE MEMBER_ID = #{memberId}
)
WHERE RN BETWEEN #{start} AND #{end}
